import React, { useEffect, useState } from 'react';
import api from '../services/api';
import SearchBar from './SearchBar';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    api.get('/books')
      .then(response => {
        setBooks(response.data);
        setFilteredBooks(response.data); // Initialize filteredBooks with all books
      })
      .catch(error => console.error(error));
  }, []);

  useEffect(() => {
    filterBooks(searchTerm, selectedCategory);
  }, [searchTerm, selectedCategory]);

  const handleSearch = (value) => {
    setSearchTerm(value.toLowerCase());
  };

  const handleCategoryChange = (event) => {
    const category = event.target.value.toLowerCase();
    setSelectedCategory(category);
  };

  const filterBooks = (term, category) => {
    const filteredBooks = books.filter(book =>
      book.name.toLowerCase().includes(term) &&
      (category === '' || book.category.toLowerCase().includes(category))
    );
    setFilteredBooks(filteredBooks);
  };

  return (
    <div>
      <SearchBar value={searchTerm} onChange={handleSearch} />
      <h1 className='text-3xl font-bold'>Book List</h1>
      <label>Select Category:</label>
      <select value={selectedCategory} onChange={handleCategoryChange}>
        <option value="">All Categories</option>
        <option value="fiction">Fiction</option>
        <option value="nonfiction">Non-Fiction</option>
        <option value="mystery">Mystery</option>
      </select>
      <ul>
        {filteredBooks.map(book => (
          <li key={book.id}>
            <h2>{book.name}</h2>
            <p><strong>Description:</strong> {book.description}</p>
            <p><strong>Page Count:</strong> {book.pageCount}</p>
            <p><strong>Category:</strong> {book.category}</p>
            <p><strong>ISBN:</strong> {book.isbn}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookList;
