import React, { useEffect, useState } from 'react';
import api from '../services/api';
import SearchBar from './Searchbar';
import Select from './Select';
import Navbar from './Navbar';
import Modal from './Modal';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [bookToDelete, setBookToDelete] = useState(null);

  const handleDeleteBook = (id) => {
    api.delete(`/books/${id}`)
      .then(response => {
        const updatedBooks = books.filter(book => book.id !== id);
        setBooks(updatedBooks);
        setFilteredBooks(updatedBooks);
      })
      .catch(error => console.error(error));
    setIsModalOpen(false);
    setBookToDelete(null);
  };

  useEffect(() => {
    api.get('/books')
      .then(response => {
        setBooks(response.data);
        setFilteredBooks(response.data);
      })
      .catch(error => console.error(error));
  }, []);

  const handleSearch = (value) => {
    setSearchTerm(value);
    filterBooks(value, selectedCategory);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category.toLowerCase());
    filterBooks(searchTerm, category.toLowerCase());
  };

  const filterBooks = (term, category) => {
    const filteredBooks = books.filter(book =>
      book.name.toLowerCase().includes(term.toLowerCase()) &&
      (category === '' || book.category.toLowerCase().includes(category))
    );
    setFilteredBooks(filteredBooks);
  };

  const openModal = (book) => {
    setIsModalOpen(true);
    setBookToDelete(book);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setBookToDelete(null);
  };

  return (
    <div className="">
      <Navbar />
      <h1 className='text-5xl font-bold text-center m-12 mb-24 text-[rgb(0,223,154)]'>Book List</h1>
      <div className='flex m-12'>
        <SearchBar onSearch={handleSearch} className=" " />
        <Select value={selectedCategory} onChange={handleCategoryChange} className="" />
      </div>
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50 dark:bg-gray-700">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-100 uppercase tracking-wider">ID</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-100 uppercase tracking-wider">Name</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-100 uppercase tracking-wider">Description</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-100 uppercase tracking-wider">Page Count</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-100 uppercase tracking-wider">Category</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-100 uppercase tracking-wider">ISBN</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-100 uppercase tracking-wider"></th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200 dark:bg-gray-800">
            {filteredBooks.map(book => (
              <tr key={book.id}>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-100">{book.id}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-100">{book.name}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-100">{book.description}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-100">{book.pageCount}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-100">{book.category}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-100">{book.isbn}</td>
                <td>
                  <button 
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-0.5 px-2 rounded" 
                    onClick={() => openModal(book)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-600 bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-xl font-bold mb-4">Confirm Deletion</h2>
            <p>Are you sure you want to delete the book titled "{bookToDelete.name}"?</p>
            <div className="mt-4">
              <button 
                className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mr-2"
                onClick={() => handleDeleteBook(bookToDelete.id)}>
                Yes, Delete
              </button>
              <button 
                className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
                onClick={closeModal}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookList;
