import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Select from './Select'; // Adjust the import path as necessary
import EditBookModal from './EditBookModal'; // Adjust the import path as necessary

const BookList = ({ books, categories }) => {
  const [filteredBooks, setFilteredBooks] = useState(books);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  useEffect(() => {
    if (selectedCategory === '') {
      setFilteredBooks(books);
    } else {
      const filtered = books.filter(book =>
        categories.find(category => category.name === selectedCategory)?.books.includes(book)
      );
      setFilteredBooks(filtered);
    }
  }, [selectedCategory, books, categories]);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const openEditModal = (book) => {
    setSelectedBook(book);
    setIsEditModalOpen(true);
  };

  const handleSaveBook = (updatedBook) => {
    // Here, you should handle updating the book in your data source.
    // For example, sending an API request or updating local state.

    // Close the modal after saving
    setIsEditModalOpen(false);
  };

  const handleCancel = () => {
    setIsEditModalOpen(false);
  };

  return (
    <div>
      <Select
        value={selectedCategory}
        onChange={handleCategoryChange}
        categories={categories}
        className="mb-4"
      />

      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ISBN</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Author</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Pages Count</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Category</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Description</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {filteredBooks.map((book) => (
            <tr key={book.id}>
              <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{book.isbn}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{book.name}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{book.author}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{book.pagesCount}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                {categories.find(category => category.books.some(b => b.id === book.id))?.name || 'N/A'}
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{book.description}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                <button
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded mr-2"
                  onClick={() => console.log('Delete functionality not implemented')}
                >
                  Delete
                </button>
                <button
                  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded"
                  onClick={() => openEditModal(book)}
                >
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <EditBookModal
        isOpen={isEditModalOpen}
        book={selectedBook}
        onSave={handleSaveBook}
        onCancel={handleCancel}
      />
    </div>
  );
};

BookList.propTypes = {
  books: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      description: PropTypes.string,
      isbn: PropTypes.string,
      author: PropTypes.string,
      pagesCount: PropTypes.number,
    })
  ).isRequired,
  categories: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      books: PropTypes.arrayOf(
        PropTypes.shape({
          id: PropTypes.string.isRequired,
        })
      ),
    })
  ).isRequired,
};

export default BookList;
