import React, { useState, useEffect } from 'react';
import api from '../services/api'; // Adjust the import path as needed

const Select = ({ value, onChange }) => {
  const [categories, setCategories] = useState([]);

  // Fetch categories when the component mounts
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await api.get('/categories');
        setCategories(response.data); // Update the state with fetched categories
      } catch (error) {
        console.error('Error fetching categories:', error);
        // Handle error as needed
      }
    };

    fetchCategories();
  }, []);

  const handleSelectChange = (event) => {
    const selectedCategory = event.target.value;
    onChange(selectedCategory); 
  };

  return (
    <form className="max-w-sm mx-auto flex-1">
      <label htmlFor="categories" className="block mb-3 text-sm font-medium text-[rgb(0,223,154)] w-full">
        Select book Category
      </label>
      <select
        id="categories"
        className="block w-full p-4 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white"
        value={value} 
        onChange={handleSelectChange}
      >
        <option value="">All Categories</option>
        {categories.map((category) => (
          <option key={category.id} value={category.id}>
            {category.categoryName}
          </option>
        ))}
      </select>
    </form>
  );
};

export default Select;
