import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Login from "../LoginRegister/Login";
import BookList from "./BookList";
import Register from "../LoginRegister/Register";

const SiteRouter = ({ user }) => {
  

  // Function to conditionally get the route based on user role
  const getRoute = (currentRole, roles, defaultPath) => {
    if (roles.includes(currentRole)) {
      return defaultPath;
    } else {
      // Redirect to login if user role is not allowed for the route
      return "/login";
    }
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route
          path={getRoute(userRole, ["user", "admin"], "/books")}
          element={<BookList />}
        />
        <Route path="/register" element={<Register />} />
        {/* Redirect to login if no matching route */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </BrowserRouter>
  );
};

export default SiteRouter;
