import React, { useEffect, useState } from 'react';
import api from '../services/api';


const BookList = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    api.get('/books')
      .then(response => {
        setBooks(response.data);
        setFilteredBooks(response.data); // Initialize filteredBooks with all books
      })
      .catch(error => console.error(error));
  }, []);

  const handleSearch = (event) => {
    const searchTerm = event.target.value.toLowerCase();
    setSearchTerm(searchTerm);
    filterBooks(searchTerm, selectedCategory);
  };

  const handleCategoryChange = (event) => {
    const category = event.target.value.toLowerCase();
    setSelectedCategory(category);
    filterBooks(searchTerm, category);
  };

  const filterBooks = (term, category) => {
    const filteredBooks = books.filter(book =>
      book.name.toLowerCase().includes(term) &&
      (category === '' || book.category.toLowerCase().includes(category))
    );
    setFilteredBooks(filteredBooks);
  };

  return (

    
    <div>
     <div class="relative overflow-x-auto">
    <table class="w-full text-sm text-left rtl:text-right text-gray-500 dark:text-gray-400">
        <thead class="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
            <tr>
                <th scope="col" class="px-6 py-3">
                    Product name
                </th>
                <th scope="col" class="px-6 py-3">
                    Color
                </th>
                <th scope="col" class="px-6 py-3">
                    Category
                </th>
                <th scope="col" class="px-6 py-3">
                    Price
                </th>
            </tr>
        </thead>
        <tbody>
            <tr class="bg-white border-b dark:bg-gray-800 dark:border-gray-700">
                <th scope="row" class="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                    Apple MacBook Pro 17"
                </th>
                <td class="px-6 py-4">
                    Silver
                </td>
                <td class="px-6 py-4">
                    Laptop
                </td>
                <td class="px-6 py-4">
                    $2999
                </td>
            </tr>
            <tr class="bg-white border-b dark:bg-gray-800 dark:border-gray-700">
                <th scope="row" class="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                    Microsoft Surface Pro
                </th>
                <td class="px-6 py-4">
                    White
                </td>
                <td class="px-6 py-4">
                    Laptop PC
                </td>
                <td class="px-6 py-4">
                    $1999
                </td>
            </tr>
            <tr class="bg-white dark:bg-gray-800">
                <th scope="row" class="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                    Magic Mouse 2
                </th>
                <td class="px-6 py-4">
                    Black
                </td>
                <td class="px-6 py-4">
                    Accessories
                </td>
                <td class="px-6 py-4">
                    $99
                </td>
            </tr>
        </tbody>
    </table>
</div>
      <h1 className='text-3xl font-bold'>Book List</h1>
      <input
        type="text"
        placeholder="Search by book name"
        value={searchTerm}
        onChange={handleSearch}
      />
      <br />
      <label>Select Category:</label>
      <select value={selectedCategory} onChange={handleCategoryChange}>
        <option value="">All Categories</option>
        <option value="fiction">Fiction</option>
        <option value="nonfiction">Non-Fiction</option>
        <option value="mystery">Mystery</option>
      </select>
      <ul>
        {filteredBooks.map(book => (
          <li key={book.id}>
            <h2>{book.name}</h2>
            <p><strong>Description:</strong> {book.description}</p>
            <p><strong>Page Count:</strong> {book.pageCount}</p>
            <p><strong>Category:</strong> {book.category}</p>
            <p><strong>ISBN:</strong> {book.isbn}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookList;
