import React from 'react';

const Select = ({ value, onChange }) => {
  const handleSelectChange = (event) => {
    onChange(event.target.value); // Passes the selected value to the parent component
  };

  return (
    <form className="max-w-sm mx-auto">
      <label htmlFor="categories" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">
        Select Category
      </label>
      <select
        id="categories"
        className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
        value={value} // The current selected value
        onChange={handleSelectChange} // Handles change event
      >
        <option value="">All Categories</option>
        <option value="fiction">Fiction</option>
        <option value="nonfiction">Non-Fiction</option>
        <option value="mystery">Mystery</option>
      </select>
    </form>
  );
};

export default Select;
