import React, { useState } from 'react';
import api from '../services/api'; // Adjust the path as per your file structure
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const AdminAddCategory = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    categoryName: '', // Renamed to match the backend field
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Create the book
      const response = await api.post('/books', {
        bookName: formData.bookName,
        bookDescription: formData.bookDescription,
        pageCount: formData.pageCount,
        categoryId: formData.categoryId, // Use categoryId for submission
        isbn: formData.isbn,
      });
  
      if (response.status === 201) {
        toast.success('Book added successfully');
  
        // If you need to link category separately, do it here
        // Example: Linking the category to the book
        await api.post('/categories', {
          bookId: response.data.id, // Assuming response.data.id is the newly created book ID
          categoryId: formData.categoryId
        });
  
        navigate("/books");
      } else {
        toast.error('Failed to add book');
      }
    } catch (error) {
      console.error('Error adding book:', error);
      toast.error('Error adding book');
    }
  };

  return (
    <section className="bg-gray-50 dark:bg-gray-900">
      <div className="flex flex-col items-center justify-center px-6 py-8 mx-auto md:h-screen lg:py-0">
        <div className="w-full bg-white rounded-lg shadow dark:border md:mt-0 sm:max-w-md xl:p-0 dark:bg-gray-800 dark:border-gray-700">
          <div className="p-6 space-y-4 md:space-y-6 sm:p-8">
            <h1 className="text-xl font-bold leading-tight tracking-tight text-gray-900 md:text-2xl dark:text-white text-center">
              Add New Book Category
            </h1>
            <form className="space-y-4 md:space-y-6" onSubmit={handleSubmit}>
              <div>
                <label htmlFor="categoryName" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Category Name</label>
                <input
                  type="text"
                  id="categoryName"
                  name="categoryName"
                  value={formData.categoryName}
                  onChange={handleChange}
                  className="bg-gray-50 border border-gray-300 text-gray-900 rounded-lg focus:ring-primary-600 focus:border-primary-600 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                  placeholder="Enter category name"
                  required
                />
              </div>
              <button
                type="submit"
                className="w-full text-white bg-blue-500 hover:bg-blue-600 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
              >
                Add
              </button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AdminAddCategory;
