import React, { useEffect, useState } from 'react';
import api from '../services/api';
import SearchBar from './SearchBar';
import Select from './Select';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    api.get('/books')
      .then(response => {
        setBooks(response.data);
        setFilteredBooks(response.data); // Initialize filteredBooks with all books
      })
      .catch(error => console.error(error));
  }, []);

  const handleSearch = (value) => {
    setSearchTerm(value);
    filterBooks(value, selectedCategory);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category.toLowerCase());
    filterBooks(searchTerm, category.toLowerCase());
  };

  const filterBooks = (term, category) => {
    const filteredBooks = books.filter(book =>
      book.name.toLowerCase().includes(term.toLowerCase()) &&
      (category === '' || book.category.toLowerCase().includes(category))
    );
    setFilteredBooks(filteredBooks);
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      <Select value={selectedCategory} onChange={handleCategoryChange} />
      <h1 className='text-3xl font-bold'>Book List</h1>
      <ul>
        {filteredBooks.map(book => (
          <li key={book.id}>
            <h2 className='text-xl font-bold'>{book.name}</h2>
            <p><strong>Description:</strong> {book.description}</p>
            <p><strong>Page Count:</strong> {book.pageCount}</p>
            <p><strong>Category:</strong> {book.category}</p>
            <p><strong>ISBN:</strong> {book.isbn}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookList;
