import React from 'react';
import { useQueryClient } from '@tanstack/react-query';

const Navbar = () => {
  const queryClient = useQueryClient();
  const username = queryClient.getQueryData('username');


  const handleLogout = () => {
    // Clear any authentication-related data (e.g., token)
    localStorage.removeItem('token');
    
    // Clear username from query cache
    queryClient.removeQueries('username');

    // Navigate to the login page
    navigate('/login');
  };
  

  return (
    <div className='bg-black flex justify-between items-center h-24 mx-auto px-4 text-white'>
      <h1 className='w-full text-3xl font-bold text-[rgb(0,223,154)]'>Book Recommendation Application</h1>
      <ul className='hidden md:flex'>
      <li className='p-4 hover:bg-[#00df9a] rounded-xl m-2 cursor-pointer duration-300 hover:text-black'>{username}</li>
    <li className='p-4 hover:bg-[#00df9a] rounded-xl m-2 cursor-pointer duration-300 hover:text-black' onClick={handleLogout}>Logout</li>
      </ul>
    </div>
  );
};

export default Navbar;
