import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Select from './Select';  // Assuming Select is a separate component
import EditBookModal from './EditBookModal';  // Assuming EditBookModal is a separate component

const BookList = ({ books, categories }) => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [filteredBooks, setFilteredBooks] = useState(books);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [selectedBook, setSelectedBook] = useState(null);

  useEffect(() => {
    // Filter books by selected category
    if (selectedCategory) {
      setFilteredBooks(
        books.filter((book) =>
          book.categoryId === selectedCategory
        )
      );
    } else {
      setFilteredBooks(books);
    }
  }, [selectedCategory, books]);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const openEditModal = (book) => {
    setSelectedBook(book);
    setIsEditModalOpen(true);
  };

  const handleSaveBook = (updatedBook) => {
    // Update the book in the list
    setFilteredBooks((prevBooks) =>
      prevBooks.map((book) =>
        book.id === updatedBook.id ? updatedBook : book
      )
    );
    setIsEditModalOpen(false);
    setSelectedBook(null);
  };

  const handleDeleteBook = (bookId) => {
    // Remove the book from the list
    setFilteredBooks((prevBooks) =>
      prevBooks.filter((book) => book.id !== bookId)
    );
  };

  const closeModal = () => {
    setIsEditModalOpen(false);
    setSelectedBook(null);
  };

  return (
    <div className="p-6">
      <Select
        value={selectedCategory}
        onChange={handleCategoryChange}
        categories={categories}
        className="mb-4"
      />

      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              ISBN
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Name
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Author
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Pages
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Category
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Description
            </th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
              Actions
            </th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {filteredBooks.map((book) => (
            <tr key={book.id}>
              <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                {book.isbn}
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                {book.name}
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                {book.author}
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                {book.pagesCount}
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                {categories.find((cat) => cat.id === book.categoryId)?.name || 'Unknown'}
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                {book.description}
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                <button
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-0.5 px-2 rounded mr-2"
                  onClick={() => handleDeleteBook(book.id)}
                >
                  Delete
                </button>
                <button
                  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-0.5 px-2 rounded"
                  onClick={() => openEditModal(book)}
                >
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <EditBookModal
        isOpen={isEditModalOpen}
        book={selectedBook}
        onSave={handleSaveBook}
        onCancel={closeModal}
      />
    </div>
  );
};

BookList.propTypes = {
  books: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
      description: PropTypes.string.isRequired,
      isbn: PropTypes.string.isRequired,
      author: PropTypes.string.isRequired,
      pagesCount: PropTypes.number.isRequired,
      categoryId: PropTypes.string.isRequired,
    })
  ).isRequired,
  categories: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      name: PropTypes.string.isRequired,
    })
  ).isRequired,
};

export default BookList;
